INSERT INTO `sutra` VALUES (1,11,'অর্জুন উবাচ<br><br>মদনুগ্রহায পরমং গুহ্যমধ্যাত্মসংজ্ঞিতম্৷<br><br>যত্ত্বযোক্তং বচস্তেন মোহোযং বিগতো মম৷৷11.1৷৷<br><br>','as','Assamese',1),(2,12,'অর্জুন উবাচ<br><br>এবং সততযুক্তা যে ভক্তাস্ত্বাং পর্যুপাসতে৷<br><br>যেচাপ্যক্ষরমব্যক্তং তেষাং কে যোগবিত্তমাঃ৷৷12.1৷৷<br><br>','as','Assamese',1),(3,14,'শ্রী ভগবানুবাচ<br><br>পরং ভূযঃ প্রবক্ষ্যামি জ্ঞানানাং জ্ঞানমুত্তমম্৷<br><br>যজ্জ্ঞাত্বা মুনযঃ সর্বে পরাং সিদ্ধিমিতো গতাঃ৷৷14.1৷৷<br><br>','as','Assamese',1),(4,6,'শ্রী ভগবানুবাচ<br><br>অনাশ্রিতঃ কর্মফলং কার্যং কর্ম করোতি যঃ৷<br><br>স সংন্যাসী চ যোগী চ ন নিরগ্নির্ন চাক্রিযঃ৷৷6.1৷৷<br><br>','as','Assamese',1),(5,4,'শ্রী ভগবানুবাচ<br><br>ইমং বিবস্বতে যোগং প্রোক্তবানহমব্যযম্৷<br><br>বিবস্বান্ মনবে প্রাহ মনুরিক্ষ্বাকবেব্রবীত্৷৷4.1৷৷<br><br>','as','Assamese',1),(6,16,'শ্রী ভগবানুবাচ<br><br>অভযং সত্ত্বসংশুদ্ধিঃ জ্ঞানযোগব্যবস্থিতিঃ৷<br><br>দানং দমশ্চ যজ্ঞশ্চ স্বাধ্যাযস্তপ আর্জবম্৷৷16.1৷৷<br><br>','as','Assamese',1),(7,7,'শ্রী ভগবানুবাচ<br><br>ময্যাসক্তমনাঃ পার্থ যোগং যুঞ্জন্মদাশ্রযঃ৷<br><br>অসংশযং সমগ্রং মাং যথা জ্ঞাস্যসি তচ্ছৃণু৷৷7.1৷৷<br><br>','as','Assamese',1),(8,8,'অর্জুন উবাচ<br><br>কিং তদ্ব্রহ্ম কিমধ্যাত্মং কিং কর্ম পুরুষোত্তম৷<br><br>অধিভূতং চ কিং প্রোক্তমধিদৈবং কিমুচ্যতে৷৷8.1৷৷<br><br>','as','Assamese',1),(9,3,'অর্জুন উবাচ<br><br>জ্যাযসী চেত্কর্মণস্তে মতা বুদ্ধির্জনার্দন৷<br><br>তত্কিং কর্মণি ঘোরে মাং নিযোজযসি কেশব৷৷3.1৷৷<br><br>','as','Assamese',1),(10,1,'ধৃতরাষ্ট্র উবাচ<br><br>ধর্মক্ষেত্রে কুরুক্ষেত্রে সমবেতা যুযুত্সবঃ৷<br><br>মামকাঃ পাণ্ডবাশ্চৈব কিমকুর্বত সঞ্জয৷৷1.1৷৷<br><br>','as','Assamese',1),(11,10,'শ্রী ভগবানুবাচ<br><br>ভূয এব মহাবাহো শ্রৃণু মে পরমং বচঃ৷<br><br>যত্তেহং প্রীযমাণায বক্ষ্যামি হিতকাম্যযা৷৷10.1৷৷<br><br>','as','Assamese',1),(12,5,'অর্জুন উবাচ<br><br>সংন্যাসং কর্মণাং কৃষ্ণ পুনর্যোগং চ শংসসি৷<br><br>যচ্ছ্রেয এতযোরেকং তন্মে ব্রূহি সুনিশ্িচতম্৷৷5.1৷৷<br><br>','as','Assamese',1),(13,15,'শ্রী ভগবানুবাচ<br><br>ঊর্ধ্বমূলমধঃশাখমশ্বত্থং প্রাহুরব্যযম্৷<br><br>ছন্দাংসি যস্য পর্ণানি যস্তং বেদ স বেদবিত্৷৷15.1৷৷<br><br>','as','Assamese',1),(14,17,'অর্জুন উবাচ<br><br>যে শাস্ত্রবিধিমুত্সৃজ্য যজন্তে শ্রদ্ধযান্বিতাঃ৷<br><br>তেষাং নিষ্ঠা তু কা কৃষ্ণ সত্ত্বমাহো রজস্তমঃ৷৷17.1৷৷<br><br>','as','Assamese',1),(15,9,'শ্রী ভগবানুবাচ<br><br>ইদং তু তে গুহ্যতমং প্রবক্ষ্যাম্যনসূযবে৷<br><br>জ্ঞানং বিজ্ঞানসহিতং যজ্জ্ঞাত্বা মোক্ষ্যসেশুভাত্৷৷9.1৷৷<br><br>','as','Assamese',1),(16,18,'অর্জুন উবাচ<br><br>সংন্যাসস্য মহাবাহো তত্ত্বমিচ্ছামি বেদিতুম্৷<br><br>ত্যাগস্য চ হৃষীকেশ পৃথক্কেশিনিষূদন৷৷18.1৷৷<br><br>','as','Assamese',1),(17,2,'সঞ্জয উবাচ<br><br>তং তথা কৃপযাবিষ্টমশ্রুপূর্ণাকুলেক্ষণম্৷<br><br>বিষীদন্তমিদং বাক্যমুবাচ মধুসূদনঃ৷৷2.1৷৷<br><br>','as','Assamese',1),(18,13,'অর্জুন উবাচ<br><br>প্রকৃতিং পুরুষং চৈব ক্ষেত্রং ক্ষেত্রজ্ঞমেব চ৷<br><br>এতদ্বেদিতুমিচ্ছামি জ্ঞানং জ্ঞেযং চ কেশব৷৷13.1৷৷<br><br>','as','Assamese',1),(19,11,'অর্জুন উবাচ<br><br>মদনুগ্রহায পরমং গুহ্যমধ্যাত্মসংজ্ঞিতম্৷<br><br>যত্ত্বযোক্তং বচস্তেন মোহোযং বিগতো মম৷৷11.1৷৷<br><br>','bn','Bengali',1),(20,12,'অর্জুন উবাচ<br><br>এবং সততযুক্তা যে ভক্তাস্ত্বাং পর্যুপাসতে৷<br><br>যেচাপ্যক্ষরমব্যক্তং তেষাং কে যোগবিত্তমাঃ৷৷12.1৷৷<br><br>','bn','Bengali',1),(21,14,'শ্রী ভগবানুবাচ<br><br>পরং ভূযঃ প্রবক্ষ্যামি জ্ঞানানাং জ্ঞানমুত্তমম্৷<br><br>যজ্জ্ঞাত্বা মুনযঃ সর্বে পরাং সিদ্ধিমিতো গতাঃ৷৷14.1৷৷<br><br>','bn','Bengali',1),(22,4,'শ্রী ভগবানুবাচ<br><br>ইমং বিবস্বতে যোগং প্রোক্তবানহমব্যযম্৷<br><br>বিবস্বান্ মনবে প্রাহ মনুরিক্ষ্বাকবেব্রবীত্৷৷4.1৷৷<br><br>','bn','Bengali',1),(23,7,'শ্রী ভগবানুবাচ<br><br>ময্যাসক্তমনাঃ পার্থ যোগং যুঞ্জন্মদাশ্রযঃ৷<br><br>অসংশযং সমগ্রং মাং যথা জ্ঞাস্যসি তচ্ছৃণু৷৷7.1৷৷<br><br>','bn','Bengali',1),(24,15,'শ্রী ভগবানুবাচ<br><br>ঊর্ধ্বমূলমধঃশাখমশ্বত্থং প্রাহুরব্যযম্৷<br><br>ছন্দাংসি যস্য পর্ণানি যস্তং বেদ স বেদবিত্৷৷15.1৷৷<br><br>','bn','Bengali',1),(25,16,'শ্রী ভগবানুবাচ<br><br>অভযং সত্ত্বসংশুদ্ধিঃ জ্ঞানযোগব্যবস্থিতিঃ৷<br><br>দানং দমশ্চ যজ্ঞশ্চ স্বাধ্যাযস্তপ আর্জবম্৷৷16.1৷৷<br><br>','bn','Bengali',1),(26,13,'অর্জুন উবাচ<br><br>প্রকৃতিং পুরুষং চৈব ক্ষেত্রং ক্ষেত্রজ্ঞমেব চ৷<br><br>এতদ্বেদিতুমিচ্ছামি জ্ঞানং জ্ঞেযং চ কেশব৷৷13.1৷৷<br><br>','bn','Bengali',1),